// Function for displaying hours
function hours(n){
  if(clockHourMode == 1){
    if(n > 12){
      n -= 12;
    }
  }

  var x = n / 10;
  var y = n % 10;

  if(x < 1){
    segment1(0);
  }else if(x >= 1 && x < 2){
    segment1(1);
  }else if(x >= 2 && x < 3){
    segment1(2);
  }

  if(y == 0){
    segment2(0);
  }else if(y == 1){
    segment2(1);
  }else if(y == 2){
    segment2(2);
  }else if(y == 3){
    segment2(3);
  }else if(y == 4){
    segment2(4);
  }else if(y == 5){
    segment2(5);
  }else if(y == 6){
    segment2(6);
  }else if(y == 7){
    segment2(7);
  }else if(y == 8){
    segment2(8);
  }else if(y == 9){
    segment2(9);
  }
}

