// Function controling meridian mode
function hourModeControl(){
  modeControlArea.addEventListener("click", function(){
    if(clockHourMode == 0){
      clockHourMode = 1;
    }else if(clockHourMode == 1){
      clockHourMode = 0;
    }
  });
}

function tick(){
  clock();
  displayHourMode();
  setTimeout('tick()', 100);
}

function init(){
  loadClock();
  separators();
  tick();
  hourModeControl();
}

